package MyProject1;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Assign by EqualSign (=)
        // Declaring variables
        int num;
        String name;
 
        // Assigning values
        num = 7;
        name = "YinZarniPoPoOo";
 
        // Displaying the assigned values
        System.out.println("num is assigned by equal sign : " + num);
        System.out.println("name is assigned by equal sign : " + name);
        
        
        //Assign by PlusEqual Sign (+=)
        // Declaring variables
        int num1 = 10, num2 = 20;
 
        System.out.println("num1 = " + num1);
        System.out.println("num2 = " + num2);
 
        // Adding & Assigning values
        num1 += num2;
 
        // Displaying the assigned values
        System.out.println("num1 after adding by plusEqual Sign with num2 : = " + num1);
        
        
        //Assign by minusEqual Sign (-=)
        // Declaring variables
        int num3 = 10, num4 = 20;
 
        System.out.println("num3 = " + num3);
        System.out.println("num4 = " + num4);
 
        // Subtracting & Assigning values
        num3 -= num4;
 
        // Displaying the assigned values
        System.out.println("num3 after using minusEqual Sign with num4 : = " + num3);
        
        
        //Assign with multiplyEqual Sign (*=)
        // Declaring variables
        int num5 = 10, num6 = 20;
 
        System.out.println("num5 = " + num5);
        System.out.println("num6 = " + num6);
 
        // Multiplying & Assigning values
        num5 *= num6;
 
        // Displaying the assigned values
        System.out.println("num5 after using multiplyEqual Sign with num6 : = " + num5);
        
        
        //Assign with divideEqual Sign (/=)
        // Declaring variables
        int num7 = 20, num8 = 10;
 
        System.out.println("num7 = " + num7);
        System.out.println("num8 = " + num8);
 
        // Dividing & Assigning values
        num7 /= num8;
 
        // Displaying the assigned values
        System.out.println("num7 after using divideEqual Sign with num8 = " + num7);
        
        
        //Assign with modulusEqual Sign (%=)
        // Declaring variables
        int num9 = 5, num10 = 3;
 
        System.out.println("num9 = " + num9);
        System.out.println("num10 = " + num10);
 
        // Moduling & Assigning values
        num9 %= num10;
 
        // Displaying the assigned values
        System.out.println("num9 after using modulusEqual Sign with num10 : = " + num9);

	}

}
